# Create the contact book dictionary
contacts = {
    "Ahmed": "01012345678",
    "Sara": "01198765432",
    "Omar": "01255544321"
}

# Print all names in the contact book
print("Contacts names:")
for name in contacts:
    print(name)

# Search for a contact by name
search_name = input("Enter a name to search: ")

if search_name in contacts:
    print("Phone number:", contacts[search_name])
else:
    print("Contact not found")

# List of students
students = [
    {"name": "Ahmed", "grades": [80, 85, 90]},
    {"name": "Sara", "grades": [75, 88, 92]},
    {"name": "Omar", "grades": [70, 78, 85]}
]

# Calculate and print average for each student
for student in students:
    average = sum(student["grades"]) / len(student["grades"])
    print(student["name"], "average grade:", average)
